import React, { useEffect, useState, useMemo } from 'react';
import { motion } from 'framer-motion';
import styled from 'styled-components';
import { useTheme } from './ThemeProvider';

const Overlay = styled(motion.div)`
  position: absolute;
  top: 0;
  bottom: 0;
  ${p => (p.$side === 'left' ? 'left: 0;' : 'right: 0;')}
  width: ${p => p.$width}px;
  pointer-events: none;
  overflow: hidden;
  z-index: 0;

  @media (max-width: 900px) {
    width: 0; /* collapse on small screens */
  }
`;

const Road = styled.div`
  position: absolute;
  left: 0;
  width: 100%;
  height: 6px;
  background: ${p => p.theme.primary}22;

  &:before {
    content: '';
    position: absolute;
    top: 50%;
    left: 0;
    width: 100%;
    height: 2px;
    transform: translateY(-50%);
    background: repeating-linear-gradient(
      to right,
      transparent 0 14px,
      ${p => p.theme.primary}99 14px 28px
    );
  }
`;

const Vehicle = styled(motion.div)`
  position: absolute;
  width: 70px;
  height: 34px;
  background: ${p => p.theme.primary};
  border-radius: 16px;
  box-shadow: 0 6px 16px ${p => p.theme.primary}33;

  &:before { /* cabin */
    content: '';
    position: absolute;
    left: 10px;
    top: -6px;
    width: 50px;
    height: 20px;
    background: ${p => p.theme.primary}cc;
    border-radius: 10px;
  }

  &:after { /* wheels */
    content: '';
    position: absolute;
    left: 6px;
    bottom: -6px;
    width: 14px;
    height: 14px;
    border-radius: 50%;
    background: ${p => p.theme.text};
    box-shadow: 46px 0 0 ${p => p.theme.text};
  }
`;

const TrafficLight = styled(motion.div)`
  position: absolute;
  width: 12px;
  height: 36px;
  border-radius: 6px;
  background: ${p => p.theme.primary}22;
  display: grid;
  place-items: center;
  padding: 2px;

  & > div {
    width: 6px;
    height: 6px;
    border-radius: 50%;
    background: ${p => p.theme.primary}77;
  }
`;

const VehicleOverlay = ({ active = true, side = 'right', width = 240 }) => {
  const { theme } = useTheme();

  const cars = useMemo(() => (
    Array.from({ length: 7 }, (_, i) => ({
      key: `car-${i}`,
      top: 12 + (i * 12) % 76,
      duration: 7 + (i % 3),
      phase: Math.random(),
    }))
  ), []);

  return (
    <Overlay
      aria-hidden
      $side={side}
      $width={width}
      initial={{ opacity: 0 }}
      animate={{ opacity: active ? 1 : 0.4 }}
      transition={{ duration: 0.4 }}
    >
      {/* Roads */}
      <Road style={{ top: '20%' }} />
      <Road style={{ top: '40%' }} />
      <Road style={{ top: '60%' }} />
      <Road style={{ top: '80%' }} />

      {/* Traffic lights */}
      <TrafficLight style={{ top: '18%', left: '6%' }}
        animate={{ opacity: [0.4, 1, 0.4] }}
        transition={{ duration: 2, repeat: Infinity }}>
        <div />
      </TrafficLight>
      <TrafficLight style={{ top: '58%', right: '8%' }}
        animate={{ opacity: [1, 0.4, 1] }}
        transition={{ duration: 2.4, repeat: Infinity, delay: 0.4 }}>
        <div />
      </TrafficLight>

      {/* Vehicles */}
      {cars.map(({ key, top, duration, phase }) => (
        <Vehicle
          key={key}
          style={{ top: `${top}%`, left: -120, willChange: 'transform', transform: 'translateZ(0)' }}
          animate={{ x: [-120, width + 180] }}
          transition={{ duration, ease: 'linear', repeat: Infinity, repeatType: 'loop', delay: -phase * duration }}
        />
      ))}
    </Overlay>
  );
};

export default VehicleOverlay;
