import React, { createContext, useContext, useMemo } from 'react';
import { ThemeProvider as StyledThemeProvider } from 'styled-components';

const ThemeContext = createContext();

const themes = {
  home: {
    primary: '#007ACC',
    secondary: '#0066cc',
    background: '#ffffff',
    text: '#1d1d1f',
    accent: '#e6f3ff',
    gradient: 'linear-gradient(135deg, #007ACC 0%, #0066cc 100%)',
    particles: '#007ACC20'
  },
  about: {
    primary: '#4A90E2',
    secondary: '#357ABD',
    background: '#f8faff',
    text: '#2c3e50',
    accent: '#e3f2fd',
    gradient: 'linear-gradient(135deg, #4A90E2 0%, #357ABD 100%)',
    particles: '#4A90E220'
  },
  experience: {
    primary: '#2E8B57',
    secondary: '#228B22',
    background: '#f0fff4',
    text: '#1a4731',
    accent: '#e8f5e8',
    gradient: 'linear-gradient(135deg, #2E8B57 0%, #228B22 100%)',
    particles: '#2E8B5720'
  },
  skills: {
    primary: '#FF6B35',
    secondary: '#E55A2B',
    background: '#fff5f0',
    text: '#2d1810',
    accent: '#ffe0d1',
    gradient: 'linear-gradient(135deg, #FF6B35 0%, #E55A2B 100%)',
    particles: '#FF6B3520'
  },
  projects: {
    primary: '#8E44AD',
    secondary: '#7D3C98',
    background: '#faf5ff',
    text: '#2c1810',
    accent: '#f3e5f5',
    gradient: 'linear-gradient(135deg, #8E44AD 0%, #7D3C98 100%)',
    particles: '#8E44AD20'
  },
  contact: {
    primary: '#E74C3C',
    secondary: '#C0392B',
    background: '#fff5f5',
    text: '#2c1810',
    accent: '#ffebee',
    gradient: 'linear-gradient(135deg, #E74C3C 0%, #C0392B 100%)',
    particles: '#E74C3C20'
  }
};

const ThemeProvider = ({ children, currentSection }) => {
  const theme = useMemo(() => {
    return themes[currentSection] || themes.home;
  }, [currentSection]);

  return (
    <StyledThemeProvider theme={theme}>
      <ThemeContext.Provider value={{ theme, currentSection }}>
        {children}
      </ThemeContext.Provider>
    </StyledThemeProvider>
  );
};

export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};

export default ThemeProvider;
