import React, { useEffect, useState, useMemo } from 'react';
import { motion } from 'framer-motion';
import styled from 'styled-components';
import { useTheme } from './ThemeProvider';

const Overlay = styled(motion.div)`
  position: absolute;
  top: 0;
  bottom: 0;
  ${p => (p.$side === 'left' ? 'left: 0;' : 'right: 0;')}
  width: ${p => p.$width}px;
  pointer-events: none;
  overflow: hidden;
  z-index: 0;

  @media (max-width: 900px) {
    width: 0; /* collapse on small screens */
  }
`;

// Replace roads/traffic lights/cars with sky elements
const Cloud = styled(motion.div)`
  position: absolute;
  width: 140px;
  height: 48px;
  background: ${p => p.theme.primary}18;
  border-radius: 24px;
  filter: saturate(0.9);

  &:before,
  &:after {
    content: '';
    position: absolute;
    background: inherit;
    border-radius: 50%;
  }
  &:before { width: 70px; height: 70px; left: 12px; top: -26px; }
  &:after  { width: 90px; height: 90px; right: 6px; top: -38px; }
`;

const Balloon = styled(motion.div)`
  position: absolute;
  width: 46px;
  height: 56px;
  border-radius: 50% 50% 45% 45%;
  background: ${p => p.theme.primary};
  box-shadow: 0 8px 18px ${p => p.theme.primary}33;

  /* highlight stripe */
  background-image: radial-gradient(ellipse at 30% 30%, rgba(255,255,255,0.25), transparent 60%);

  /* rope */
  &:before {
    content: '';
    position: absolute;
    left: 50%;
    bottom: -16px;
    width: 2px;
    height: 14px;
    background: ${p => p.theme.primary};
    transform: translateX(-50%);
  }
  /* basket */
  &:after {
    content: '';
    position: absolute;
    left: 50%;
    bottom: -26px;
    width: 18px;
    height: 12px;
    background: ${p => p.theme.primary}cc;
    border-radius: 2px;
    transform: translateX(-50%);
    box-shadow: 0 4px 10px ${p => p.theme.primary}22;
  }
`;

const VehicleOverlay = ({ active = true, side = 'right', width = 240 }) => {
  const { theme } = useTheme();
  const [height, setHeight] = useState(typeof window !== 'undefined' ? window.innerHeight : 800);

  useEffect(() => {
    const onResize = () => setHeight(window.innerHeight);
    window.addEventListener('resize', onResize);
    return () => window.removeEventListener('resize', onResize);
  }, []);

  // floating balloons
  const balloons = useMemo(() => (
    Array.from({ length: 6 }, (_, i) => ({
      key: `balloon-${i}`,
      left: 10 + (i * 15) % 80, // percentage within gutter
      duration: 14 + (i % 4) * 2,
      phase: Math.random(), // negative delay to desync
    }))
  ), []);

  // slow drifting clouds
  const clouds = useMemo(() => (
    Array.from({ length: 3 }, (_, i) => ({
      key: `cloud-${i}`,
      top: 10 + i * 28, // %
      duration: 28 + i * 6,
      delay: i * 1.5,
    }))
  ), []);

  return (
    <Overlay
      aria-hidden
      $side={side}
      $width={width}
      initial={{ opacity: 0 }}
      animate={{ opacity: active ? 1 : 0.4 }}
      transition={{ duration: 0.4 }}
    >
      {/* Clouds */}
      {clouds.map(({ key, top, duration, delay }) => (
        <Cloud
          key={key}
          style={{ top: `${top}%`, left: -160 }}
          animate={{ x: [-160, width + 200] }}
          transition={{ duration, delay, ease: 'linear', repeat: Infinity }}
        />
      ))}

      {/* Balloons */}
      {balloons.map(({ key, left, duration, phase }) => (
        <Balloon
          key={key}
          style={{ top: 0, left: `${left}%`, willChange: 'transform', transform: 'translateZ(0)' }}
          animate={{ y: [height + 220, -240], x: [-8, 8, -8] }}
          transition={{ duration, ease: 'linear', repeat: Infinity, repeatType: 'loop', delay: -phase * duration }}
        />
      ))}
    </Overlay>
  );
};

export default VehicleOverlay;
