import React from 'react';
import { motion } from 'framer-motion';
import styled from 'styled-components';
import { useTheme } from './ThemeProvider';
import { useInView } from 'react-intersection-observer';

const Section = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  background: ${props => props.theme.background};
  color: ${props => props.theme.text};
  padding: 4rem 0;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const SectionTitle = styled(motion.h2)`
  font-size: 3rem;
  text-align: center;
  margin-bottom: 3rem;
  background: ${props => props.theme.gradient};
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const Timeline = styled.div`
  position: relative;
  max-width: 800px;
  margin: 0 auto;
`;

const TimelineItem = styled(motion.div)`
  margin-bottom: 3rem;
  padding: 2rem;
  background: ${props => `${props.theme.primary}10`};
  border-radius: 20px;
  border-left: 4px solid ${props => props.theme.primary};
`;

const Experience = () => {
  const { ref, inView } = useInView({ threshold: 0.3 });

  const experiences = [
    {
      period: "May 2025 - Present",
      title: "Senior Consultant",
      company: "Mantel Group, Melbourne",
      description: "Cloud migration initiative to lift and shift on-premises infrastructure to AWS"
    },
    {
      period: "Nov 2022 - May 2025",
      title: "Senior Engineer (Platform)",
      company: "Versent Pty Ltd, Melbourne",
      description: "Support and enhance Woodside Energy's asset management suite"
    }
  ];

  return (
    <Section id="experience" ref={ref}>
      <Container>
        <SectionTitle
          initial={{ opacity: 0, y: 30 }}
          animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
        >
          Experience
        </SectionTitle>
        <Timeline>
          {experiences.map((exp, index) => (
            <TimelineItem
              key={index}
              initial={{ opacity: 0, x: -50 }}
              animate={inView ? { opacity: 1, x: 0 } : { opacity: 0, x: -50 }}
              transition={{ delay: index * 0.2 }}
            >
              <div style={{ fontSize: '0.9rem', opacity: 0.8, marginBottom: '0.5rem' }}>
                {exp.period}
              </div>
              <h3>{exp.title}</h3>
              <h4 style={{ opacity: 0.9, marginBottom: '1rem' }}>{exp.company}</h4>
              <p>{exp.description}</p>
            </TimelineItem>
          ))}
        </Timeline>
      </Container>
    </Section>
  );
};

export default Experience;
